                    );
                    
                    if ( count  ( $matches ) > 0 )
                        {
                            reset ( $matches );
                            $domain_extension   =   current ( $matches );   
                            
                            $domain = str_replace( "." . $domain_extension, "", $domain);
                            $arr    = explode( '.', $domain );
                            
                            $arr    =   (array)$arr[ count ( $arr ) - 1 ];
                            $arr[]  =   $domain_extension;
                            
                            return join('.', $arr);
                        }
                    
                    $arr = array_slice(array_filter(explode('.', $domain, 4), function($value){
                        return $value !== 'www';
                    }), 0); //rebuild array indexes
                        
                    if (count($arr) > 2)
                        {
                            $count = count($arr);
                            $_sub = explode('.', $count === 4 ? $arr[3] : $arr[2]);

                            if (count($_sub) === 2) // two level TLD
                            {
                                $removed = array_shift($arr);
                                if ($count === 4) // got a subdomain acting as a domain
                                {
                                    $removed = array_shift($arr);
                                }
                            }
                            elseif (count($_sub) === 1) // one level TLD
                            {
                                $removed = array_shift($arr); //remove the subdomain
                                if (strlen($_sub[0]) === 2 && $count === 3) // TLD domain must be 2 letters
                                {
                                    array_unshift($arr, $removed);
                                }
                                else
                                {
                                    // non country TLD according to IANA
                                    $tlds = array(
                                        'aero',
                                        'arpa',
                                        'asia',
                                        'biz',
                                        'cat',
                                        'com',
                                        'coop',
                                        'edu',
                                        'gov',
                                        'info',
                                        'jobs',
                                        'mil',
                                        'mobi',
                                        'museum',
                                        'name',
                                        'net',
                                        'org',
                                        'post',
                                        'pro',
                                        'tel',
                                        'travel',
                                        'xxx',
                                    );
                                    if (count($arr) > 2 && in_array($_sub[0], $tlds) !== false) //special TLD don't have a country
                                    {
                                        array_shift($arr);
                                    }
                                }
                            }
                            else // more than 3 levels, something is wrong
                            {
                                for ($i = count($_sub); $i > 1; $i--)
                                {
                                    $removed = array_shift($arr);
                                }
                            }
                        }
                    elseif (count($arr) === 2)
                        {
                            $arr0 = array_shift($arr);
                            if (strpos(join('.', $arr), '.') === false
                                && in_array($arr[0], array(
                                                                //'localhost',
                                                                //'test',
                                                                'invalid'
                                                                )) === false) // not a reserved domain
                            {
                                // seems invalid domain, restore it
                                array_unshift($arr, $arr0);
                            }
                        }
                        
                    return join('.', $arr);
                    
                }   
            
        }


    
    if ( ! function_exists( 'woocommerce_account_orders' ) ) 
        {

            /**
             * My Account > Orders template.
             *
             * @param int $current_page Current page number.
             */
            function woocommerce_account_orders( $current_page ) 
                {
                    if (    !   class_exists('WooGC_Template'))
                        return;
                        
                    $WooGC_Template =   new WooGC_Template();
                    $WooGC_Template->woocommerce_account_orders( $current_page );
                    
                }
        }
    
        
        
    if (!function_exists('WooGC_write_log'))
        {    
            function WooGC_write_log($text)
                {
                    $myFile = "log-file.txt";
                    $fh = fopen($myFile, 'a') or die("can't open file");
                    fwrite($fh, date("Y-m-d H:i:s") . " - " .$text . "\n");
                    fclose($fh);            
                }
        }

